void display_settings::deploy_luts(const std::string& lut_directory)
{
	const std::experimental::filesystem::path lut_directory_fs(lut_directory);
	for (auto&& item : luts)
	{
		const std::experimental::filesystem::path file(item.name + ".lut");
		const auto full_path = lut_directory_fs / file;
		const auto file_name = std::experimental::filesystem::absolute(full_path).string();
		std::ofstream stream(file_name);
		for (size_t i = 0; i < item.data.size() / 3; ++i)
		{
			const auto idx = 3 * i;
			stream << static_cast<int>(item.data.at(0 + idx)) << " " << static_cast<int>(item.data.at(1 + idx)) << " " << static_cast<int>(item.data.at(2 + idx)) << std::endl;
		}
	}
}
